            <label for=\"exampleInputEmail1\" class=\"form-label\">Nouveau mot de passe</label>
            <input name=\"newPassword\" type=\"password\" class=\"form-control\" id=\"exampleInputEmail1\"
                aria-describedby=\"emailHelp\" placeholder=\"Saisissez votre nouveau mot de passe\">
        </div>
        <div class=\"mb-3\">
